import React from 'react';
import PROJECTS from '../service/projects';
import './ProjectPage.css';

export default function FrontEndProj({id}) {
  const renderProject = () => {
    const projeto = PROJECTS.filter((project) => project.id === 'purpleRain');
    return(
      <>
        <h1>{projeto[0].title}</h1>
        <div className="project-card">
          <img src={projeto[0].img} alt={projeto[0].title} className="project-pic"/>
          <div className="project-info">
            <p>{projeto[0].text}</p>
          </div>
        </div>
        <section className="project-links">
        <div>
        <p>{projeto[0].pageLink}</p>
        </div>
        <p>{projeto[0].repoLink}</p>
        </section>
      </>
    );
  }

  return (
    <section className="projects-container">
      {renderProject()}
    </section>
  );
}
