import React, { useState } from 'react';
import Fade from 'react-reveal/Fade';
import PROJECTS from '../service/projects';
import './FFNav.css';

export default function FFNav({setPage}) {
  const [date, setDate] = useState(new Date());

  const getTime = () => {
    setTimeout(()=> setDate(new Date()), 1000);
    let times = date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds();
    return times;
  }

  return (
    <Fade right duration={200}>
      <aside>
        <nav className="side-menu">
          <p onClick={() => setPage('About')} className="link">Sobre Mim</p>
          <p>Projetos</p>
          <ul>
            { PROJECTS.map(({title, id}) => <li key={id} onClick={ () => setPage(id)}> { title } </li>) }
          </ul>
        </nav>
        <div className="timer-container">
          <p>Time {getTime()}</p>
        </div>
      </aside>
    </Fade>
  )
}
