import React, { useEffect, useState } from 'react';
import Fade from 'react-reveal/Fade';
import './FFNav.css';

export default function FFNav() {
      let today = new Date();
  const [time, setTime] = useState(today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds());

  useEffect(()=> {
  }, [time])

  const getTime = () => {
    let today = new Date();
    let times = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    return setTime(times);
  }

  return (
    <Fade right duration={200}>
      <aside>
        <nav className="side-menu">
          <h2>Menu</h2>
          <h4>Item 1</h4>
          <h4>Item 2</h4>
        </nav>
        <div className="timer-container">
          <h3>Time</h3>
          <p>{time}</p>
        </div>
      </aside>
    </Fade>
  )
}
