import React, { useEffect, useState } from 'react';
import Fade from 'react-reveal/Fade';
import './FFNav.css';

export default function FFNav() {
  const [date, setDate] = useState(new Date());

  const getTime = () => {
    let times = date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds();
    return setDate(times);
  }

  return (
    <Fade right duration={200}>
      <aside>
        <nav className="side-menu">
          <h2>Menu</h2>
          <h4>Item 1</h4>
          <h4>Item 2</h4>
        </nav>
        <div className="timer-container">
          <h3>Time</h3>
          {setTimeout(()=> setDate(new Date()))}
          <p>{date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds()}</p>
          <p></p>
        </div>
      </aside>
    </Fade>
  )
}
